package jgonzalezfernandez.mazes

import eu.timepit.refined.auto._
import jgonzalezfernandez.mazes.Utils.TwoTo100
import jgonzalezfernandez.mazes.algorithms.GenerationAlgorithm
import jgonzalezfernandez.mazes.grids.GridType
import org.scalatest.wordspec.AnyWordSpec

import java.io.File
import java.nio.file.Files
import javax.xml.bind.DatatypeConverter

class MazeSpec extends AnyWordSpec {
  val rows: TwoTo100    = 10
  val columns: TwoTo100 = 10
  val seed              = 0
  val fileName          = "test"

  def fileToString(file: File): String = DatatypeConverter.printBase64Binary(Files.readAllBytes(file.toPath))

  "class constructor" when {

    "using the RecursiveDivision algorithm" should afterWord("fail if the gridType is") {

      "Circular" in {
        assertThrows[IllegalArgumentException](new Maze(GridType.Circular, GenerationAlgorithm.RecursiveDivision, Some(rows), Some(columns)))
      }

      "Hexagonal" in {
        assertThrows[IllegalArgumentException](new Maze(GridType.Hexagonal, GenerationAlgorithm.RecursiveDivision, Some(rows), Some(columns)))
      }

      "Triangular" in {
        assertThrows[IllegalArgumentException](new Maze(GridType.Triangular, GenerationAlgorithm.RecursiveDivision, Some(rows), Some(columns)))
      }
    }
  }

  // These tests are mainly used to avoid regressions during refactorings from a state that has already been confirmed to be correct
  "makePng" when afterWord("the gridType is") {

    val produceValidMaze = afterWord("produce a valid maze if the generationAlgorithm is")

    "Circular" should produceValidMaze {

      "BinaryTree" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Circular, GenerationAlgorithm.BinaryTree, Some(rows)).makePng(fileName)) == expected)
      }

      "Sidewinder" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Circular, GenerationAlgorithm.Sidewinder, Some(rows)).makePng(fileName)) == expected)
      }

      "RecursiveBacktracker" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Circular, GenerationAlgorithm.RecursiveBacktracker, Some(rows)).makePng(fileName)) == expected)
      }
    }

    "Hexagonal" should produceValidMaze {

      "BinaryTree" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Hexagonal, GenerationAlgorithm.BinaryTree, Some(rows), Some(columns)).makePng(fileName)) == expected)
      }

      "Sidewinder" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Hexagonal, GenerationAlgorithm.Sidewinder, Some(rows), Some(columns)).makePng(fileName)) == expected)
      }

      "RecursiveBacktracker" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Hexagonal, GenerationAlgorithm.RecursiveBacktracker, Some(rows), Some(columns)).makePng(fileName)) == expected)
      }
    }

    "Square" should produceValidMaze {

      "BinaryTree" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Square, GenerationAlgorithm.BinaryTree, Some(rows), Some(columns)).makePng(fileName)) == expected)
      }

      "Sidewinder" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Square, GenerationAlgorithm.Sidewinder, Some(rows), Some(columns)).makePng(fileName)) == expected)
      }

      "RecursiveBacktracker" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Square, GenerationAlgorithm.RecursiveBacktracker, Some(rows), Some(columns)).makePng(fileName)) == expected)
      }

      "RecursiveDivision" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Square, GenerationAlgorithm.RecursiveDivision, Some(rows), Some(columns)).makePng(fileName)) == expected)
      }
    }

    "Triangular" should produceValidMaze {

      "BinaryTree" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Triangular, GenerationAlgorithm.BinaryTree, Some(rows), Some(columns)).makePng(fileName)) == expected)
      }

      "Sidewinder" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Triangular, GenerationAlgorithm.Sidewinder, Some(rows), Some(columns)).makePng(fileName)) == expected)
      }

      "RecursiveBacktracker" in {
        Utils.random.setSeed(seed)
        val expected =
          ""
        assert(fileToString(new Maze(GridType.Triangular, GenerationAlgorithm.RecursiveBacktracker, Some(rows), Some(columns)).makePng(fileName)) == expected)
      }
    }
  }

}
